import {
  WEATHER_API_KEY,
  OPEN_WEATHER_API_KEY,
  OPEN_WEATHER_BASE_URL,
} from "../constants/api";
import Axios from "./main/mainApi";

const getCurrentWeather = async (searchValue) => {
  return Axios.get(
    `/current.json?q=${searchValue.name}&key=${WEATHER_API_KEY}`
  );
};

const getForecastWeather = async (searchValue) => {
  // return Axios.get(
  //   `/data/2.5/weather?q=${searchValue}&appid=${OPEN_WEATHER_API_KEY}`,
  //   {
  //     baseURL: OPEN_WEATHER_BASE_URL,
  //   }
  // );

  return Axios.get(
    `/data/2.5/weather?q=${searchValue}&appid=${OPEN_WEATHER_API_KEY}`
  );
};

const weatherApi = {
  getCurrentWeather,
  getForecastWeather,
};

export default weatherApi;
