import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { selectSearchValue } from "../../redux/locationSearch/locationSearchSlice";
import {
  getCurrentWeather,
  getWeatherData,
  selectWeatherCurrentData,
  selectWeatherData,
} from "../../redux/weather/weatherSlice";
import Box from "@mui/material/Box";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import WeatherCard from "../shared/WeatherCard";
import Avatar from "@mui/material/Avatar";
import LabelAndValue from "../shared/LabelAndValue";
import { getWindDirectionText } from "../../helpers/app";

const Now = () => {
  const searchValue = useSelector(selectSearchValue);
  const dispatch = useDispatch();
  const {
    temp_c,
    condition: { text, icon } = {},
    pressure_mb,
    humidity,
    wind_kph,
    wind_dir,
    cloud,
    feelslike_c,
    vis_km,
  } = useSelector(selectWeatherCurrentData) || {};

  useEffect(() => {
    if (searchValue) {
      dispatch(getWeatherData(searchValue));
    }
  }, [searchValue, dispatch]);

  return (
    <WeatherCard>
      <CardContent>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
          {text}
        </Typography>
        <Avatar
          sx={{ width: 64, height: 64 }}
          variant="square"
          src={`https:${icon}`}
        />
        <LabelAndValue
          label="Temperature"
          value={temp_c}
          additionalValue=" &#x2103;"
        />
        <LabelAndValue
          label="Real Feel"
          value={feelslike_c}
          additionalValue=" &#x2103;"
        />
        <LabelAndValue
          label="Pressure"
          value={pressure_mb}
          additionalValue=" MB"
        />
        <LabelAndValue label="Humidity" value={humidity} additionalValue=" %" />
        <LabelAndValue
          label="Wind speed"
          value={wind_kph}
          additionalValue=" k/h"
        />
        <LabelAndValue
          label="Wind direction"
          value={getWindDirectionText(wind_dir)}
        />
        <LabelAndValue label="Cloud" value={cloud} />
        <LabelAndValue
          label="Visibility"
          value={vis_km}
          additionalValue=" km"
        />
      </CardContent>
    </WeatherCard>
  );
};

export default Now;
