import React, { useEffect, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { selectSearchValue } from "../../redux/locationSearch/locationSearchSlice";
import {
  getCurrentWeather,
  getWeatherData,
  selectWeatherCurrentData,
  selectWeatherData,
} from "../../redux/weather/weatherSlice";
import Box from "@mui/material/Box";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import WeatherCard from "../shared/WeatherCard";
import Avatar from "@mui/material/Avatar";
import LabelAndValue from "../shared/LabelAndValue";
import { getWindDirectionText } from "../../helpers/app";
import Grid from "@mui/material/Grid";

const Now = () => {
  const searchValue = useSelector(selectSearchValue);
  const dispatch = useDispatch();
  const weatherData = useSelector(selectWeatherCurrentData) || {};
  const { condition: { text, icon } = {} } = weatherData;

  const weatherDataArray = useMemo(() => {
    const {
      temp_c,

      pressure_mb,
      humidity,
      wind_kph,
      wind_dir,
      cloud,
      feelslike_c,
      vis_km,
    } = weatherData;
  }, [weatherData]);

  useEffect(() => {
    if (searchValue) {
      dispatch(getWeatherData(searchValue));
    }
  }, [searchValue, dispatch]);

  return (
    <WeatherCard>
      <CardContent>
        <Grid
          container
          spacing={{ xs: 2, md: 3 }}
          columns={{ xs: 4, sm: 8, md: 12 }}
        >
          {Array.from(Array(6)).map((_, index) => (
            <Grid item xs={2} sm={4} md={4} key={index}>
              <LabelAndValue
                label={text}
                value={
                  <Avatar
                    sx={{ width: 64, height: 64 }}
                    variant="square"
                    src={`https:${icon}`}
                  />
                }
              />
            </Grid>
          ))}
        </Grid>

        <LabelAndValue
          label="Temperature"
          value={temp_c}
          additionalValue=" &#x2103;"
        />
        <LabelAndValue
          label="Real Feel"
          value={feelslike_c}
          additionalValue=" &#x2103;"
        />
        <LabelAndValue
          label="Pressure"
          value={pressure_mb}
          additionalValue=" MB"
        />
        <LabelAndValue label="Humidity" value={humidity} additionalValue=" %" />
        <LabelAndValue
          label="Wind speed"
          value={wind_kph}
          additionalValue=" k/h"
        />
        <LabelAndValue
          label="Wind direction"
          value={getWindDirectionText(wind_dir)}
        />
        <LabelAndValue label="Cloud" value={cloud} />
        <LabelAndValue
          label="Visibility"
          value={vis_km}
          additionalValue=" km"
        />
      </CardContent>
    </WeatherCard>
  );
};

export default Now;
