import { OPEN_WEATHER_API_KEY } from "../constants/api";
import Axios from "../main/mainApi";

const searchLocationApi = async (searchValue) => {
  return Axios.get(
    `/geo/1.0/direct?q=${searchValue}&appid=${OPEN_WEATHER_API_KEY}`
  );
};

const weather = {
  searchLocationApi,
};

export default weather;
