import { OPEN_WEATHER_API_KEY } from "../constants/api";
import Axios from "./main/mainApi";

const searchLocation = async (searchValue) => {
  return Axios.get(`/search.json?q=${searchValue}&key=${OPEN_WEATHER_API_KEY}`);
  // return Axios.get(
  //   `/maps/api/place/findplacefromtext/json?input=${searchValue}`,
