import { CardContent } from "@mui/material";
import React, { useEffect, useMemo } from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { selectSearchValue } from "../../redux/locationSearch/locationSearchSlice";
import {
  getWeatherData,
  selectWeatherForecastData,
} from "../../redux/weather/weatherSlice";
import WeatherCard from "../shared/WeatherCard";

const Today = () => {
  const searchValue = useSelector(selectSearchValue);
  const weatherData = useSelector(selectWeatherForecastData) || [];
  const today = useMemo(
    () =>
      weatherData.find(
        (d) => new Date(d.date).getDate() === new Date().getDate()
      ),
    [weatherData]
  );
  console.log(weatherData);
  const dispatch = useDispatch();

  useEffect(() => {
    if (searchValue) {
      dispatch(getWeatherData(searchValue));
    }
  }, [searchValue, dispatch]);

  return (
    <WeatherCard>
      <CardContent></CardContent>
    </WeatherCard>
  );
};

export default Today;
