import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import weatherApi from "../../api/weather";

const initialState = {
  searchValue: "",
  options: [],
  loading: false,
};

export const searchLocation = createAsyncThunk(
  "weather/searchLocation",
  async (searchValue) => {
    const response = await weatherApi.searchLocation(searchValue);
    // The value we return becomes the `fulfilled` action payload
    return response.data;
  }
);

export const weatherSlice = createSlice({
  name: "weather",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(searchLocation.pending, (state) => {
        state.loading = true;
      })
      .addCase(searchLocation.fulfilled, (state, action) => {
        state.loading = false;
        state.options = action.payload.filter(
          ({ lat, lon }, i, arr) =>
            arr.findIndex((d) => d.lat === lat) === i &&
            arr.findIndex((d) => d.lon === lon) === i
        );
      });
  },
});

export const { incrementByAmount } = weatherSlice.actions;

export default weatherSlice.reducer;
