import { OPEN_WEATHER_API_KEY } from "../constants/api";
import Axios from "./main/mainApi";

const getCurrentWeather = async (searchValue) => {
  return Axios.get(
    `/current.json?q=${searchValue.name}&key=${OPEN_WEATHER_API_KEY}`
  );
};

const getForecastWeather = async (searchValue) => {
  return Axios.get(
    `/forecast.json?q=${searchValue.name}&key=${OPEN_WEATHER_API_KEY}`
  );
};

const weatherApi = {
  getCurrentWeather,
  getForecastWeather,
};

export default weatherApi;
