import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import weatherApi from "../../api/weather";

const initialState = {
  searchValue: "",
  options: [],
  loading: false,
};

export const searchLocation = createAsyncThunk(
  "weather/searchLocation",
  async (searchValue) => {
    const response = await weatherApi.searchLocation(searchValue);
    // The value we return becomes the `fulfilled` action payload
    console.log(response.data.map((d) => d.name).join());
    return response.data;
  }
);

export const weatherSlice = createSlice({
  name: "weather",
  initialState,
  reducers: {
    incrementByAmount: (state, action) => {
      state.value += action.payload;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(searchLocation.pending, (state) => {
        state.loading = true;
      })
      .addCase(searchLocation.fulfilled, (state, action) => {
        state.loading = false;
        state.options = action.payload;
      });
  },
});

export const { incrementByAmount } = weatherSlice.actions;

// The function below is called a selector and allows us to select a value from
// the state. Selectors can also be defined inline where they're used instead of
// in the slice file. For example: `useSelector((state: RootState) => state.counter.value)`
export const selectOptions = (state) => state.weather.options;
export const selectIsLoading = (state) => state.weather.loading;

// We can also write thunks by hand, which may contain both sync and async logic.
// Here's an example of conditionally dispatching actions based on current state.

// export const incrementIfOdd = (amount) => (dispatch, getState) => {
//   const currentValue = selectCount(getState());
//   if (currentValue % 2 === 1) {
//     dispatch(incrementByAmount(amount));
//   }
// };

export default weatherSlice.reducer;
