import { OPEN_WEATHER_API_KEY } from "../constants/api";
import Axios from "./main/mainApi";

const getWeather = async (searchValue) => {
  return Axios.get(
    `/current.json?q=${searchValue.name}&key=${OPEN_WEATHER_API_KEY}`
  );
};

const weatherApi = {
  getWeather,
};

export default weatherApi;
