import React, { useEffect, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { selectSearchValue } from "../../redux/locationSearch/locationSearchSlice";
import {
  getCurrentWeather,
  getWeatherData,
  selectWeatherCurrentData,
  selectWeatherData,
} from "../../redux/weather/weatherSlice";
import Box from "@mui/material/Box";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import WeatherCard from "../shared/WeatherCard";
import Avatar from "@mui/material/Avatar";
import LabelAndValue from "../shared/LabelAndValue";
import { getWindDirectionText } from "../../helpers/app";
import Grid from "@mui/material/Grid";
import { WEATHER_DATA_KEYS } from "../../constants/app";

const Now = () => {
  const searchValue = useSelector(selectSearchValue);
  const dispatch = useDispatch();
  const weatherData = useSelector(selectWeatherCurrentData) || {};
  const { condition: { text, icon } = {} } = weatherData;

  useEffect(() => {
    if (searchValue) {
      dispatch(getWeatherData(searchValue));
    }
  }, [searchValue, dispatch]);

  return (
    <WeatherCard>
      <CardContent>
        <Grid
          container
          spacing={{ xs: 2, md: 3 }}
          columns={{ xs: 4, sm: 8, md: 12 }}
        >
          <>
            <Grid item xs={2} sm={4} md={4}>
              <LabelAndValue
                label={text}
                value={
                  <Avatar
                    sx={{ width: 64, height: 64 }}
                    variant="square"
                    src={`https:${icon}`}
                  />
                }
              />
            </Grid>
            {WEATHER_DATA_KEYS.map(({ key, label, additionalValue }) => (
              <Grid item xs={2} sm={4} md={4} key={key}>
                <LabelAndValue
                  label={label}
                  additionalValue={" &#x2103;"}
                  value={weatherData[key]}
                />
              </Grid>
            ))}
          </>
        </Grid>

        {/* <LabelAndValue label="" value={temp_c} additionalValue="" />
        <LabelAndValue label="" value={feelslike_c} additionalValue="" />
        <LabelAndValue label="" value={pressure_mb} additionalValue="" />
        <LabelAndValue label="" value={humidity} additionalValue="" />
        <LabelAndValue label="" value={wind_kph} additionalValue="" />
        <LabelAndValue label="" value={getWindDirectionText(wind_dir)} />
        <LabelAndValue label="" value={cloud} />
        <LabelAndValue label="" value={vis_km} additionalValue="" /> */}
      </CardContent>
    </WeatherCard>
  );
};

export default Now;
