import React, { useCallback, useEffect, useState } from "react";
import { styled, alpha } from "@mui/material/styles";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import CircularProgress from "@mui/material/CircularProgress";
import SearchIcon from "@mui/icons-material/Search";
import InputBase from "@mui/material/InputBase";
import { useDispatch } from "react-redux";
import {
  searchLocation,
  selectIsLoading,
  selectData,
} from "../../redux/locationSearch/locationSearchSlice";
import { useSelector } from "react-redux";
import debounce from "lodash/debounce";
import {
  selectSearchText,
  selectSearchValue,
  setSearchText,
  setSearchValue,
} from "../../redux/locationSearch/locationSearchSlice";

const SearchWrapper = styled("div")(({ theme }) => ({
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  "&:hover": {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginLeft: 0,
  width: "100%",
}));

const SearchIconWrapper = styled("div")(({ theme }) => ({
  padding: theme.spacing(1, 1),
  position: "absolute",
  pointerEvents: "none",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: "inherit",
  "& .MuiInputBase-input": {
    padding: theme.spacing(1, 1, 1, 0),
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    paddingRight: 30,
  },
}));

const StyledCircularProgress = styled(CircularProgress)(() => ({
  position: "absolute",
  right: 30,
  top: 20,
}));

export default function Seacrh() {
  const dispatch = useDispatch();
  const [open, setOpen] = useState(false);
  const [debouncedSearchValue, setdebouncedSearchValue] = useState("");
  const loading = useSelector(selectIsLoading);
  const options = useSelector(selectData);
  const searchText = useSelector(selectSearchText);
  const value = useSelector(selectSearchValue);

  const updateDebouncedSearch = useCallback(
    debounce(setdebouncedSearchValue, 300),
    [setdebouncedSearchValue]
  );

  useEffect(() => {
    updateDebouncedSearch(searchText);
  }, [searchText, updateDebouncedSearch]);

  useEffect(() => {
    if (debouncedSearchValue) {
      dispatch(searchLocation(debouncedSearchValue));
    }
  }, [debouncedSearchValue, dispatch]);

  const onInputChange = (e) => {
    if (value) {
      dispatch(setSearchValue(null));
    }
    dispatch(setSearchText(e.target.value));
  };

  const onValueChange = (e, o) => {
    dispatch(setSearchValue(o));
  };

  return (
    <Autocomplete
      value={value}
      onChange={onValueChange}
      disableClearable
      freeSolo
      sx={{ width: 300 }}
      open={open || true}
      onOpen={() => {
        console.log("Open");
        setOpen(true);
      }}
      onClose={() => {
        console.log("Close");
        setOpen(false);
      }}
      isOptionEqualToValue={(option, value) => {
        console.log(option, value);
        return option.id === value.id;
      }}
      getOptionLabel={(option) => option.name}
      options={options}
      loading={loading}
      renderInput={(params) => {
        const { InputLabelProps, InputProps, ...rest } = params;
        return (
          <SearchWrapper>
            <SearchIconWrapper>
              <SearchIcon />
            </SearchIconWrapper>
            <StyledInputBase
              {...params.InputProps}
              {...rest}
              placeholder="Search"
              value={searchText}
              onChange={onInputChange}
            />
            {loading ? (
              <StyledCircularProgress color="inherit" size={20} />
            ) : null}
          </SearchWrapper>
        );
      }}
    />
  );
}
