import { Avatar, CardContent, Grid, Typography } from "@mui/material";
import React, { useEffect, useMemo } from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { WEATHER_SHORT_DATA_OBJECTS } from "../../constants/app";
import { selectSearchValue } from "../../redux/locationSearch/locationSearchSlice";
import {
  getWeatherData,
  selectWeatherForecastData,
} from "../../redux/weather/weatherSlice";
import LabelAndValue from "../shared/LabelAndValue";
import WeatherCard from "../shared/WeatherCard";

const Today = () => {
  const searchValue = useSelector(selectSearchValue);
  const { forecastday } = useSelector(selectWeatherForecastData) || [];
  const hours = useMemo(
    () =>
      (
        forecastday.find(
          (d) => new Date(d.date).getDate() === new Date().getDate()
        ) || {}
      ).hour || [],
    [forecastday]
  );
  console.log(hours);
  const dispatch = useDispatch();

  useEffect(() => {
    if (searchValue) {
      dispatch(getWeatherData(searchValue));
    }
  }, [searchValue, dispatch]);

  return (
    <WeatherCard>
      <CardContent>
        <Grid container spacing={{ xs: 1, md: 2 }}>
          <>
            {hours.map((h) => {
              const { condition: { text, icon } = {}, time, temp_c } = h;
              return (
                <Grid item xs={2} sm={1} key={time}>
                  <Avatar
                    sx={{ width: 32, height: 32 }}
                    variant="square"
                    src={`https:${icon}`}
                  />
                  <Typography>{text}</Typography>
                  <Typography>{temp_c}</Typography>
                  {/* {WEATHER_SHORT_DATA_OBJECTS.map(
                    ({ key, label, additionalValue, valueRenderer }) => (
                      <LabelAndValue
                        label={label}
                        additionalValue={additionalValue}
                        value={valueRenderer ? valueRenderer(h[key]) : h[key]}
                      />
                    )
                  )} */}
                </Grid>
              );
            })}
          </>
        </Grid>
      </CardContent>
    </WeatherCard>
  );
};

export default Today;
