import { Avatar, CardContent, Grid, Paper, Typography } from "@mui/material";
import React, { useEffect } from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { selectSearchValue } from "../../redux/locationSearch/locationSearchSlice";
import {
  getWeatherData,
  selectWeatherForecastData,
} from "../../redux/weather/weatherSlice";
import WeatherCard from "../shared/WeatherCard";
import m from "moment";
import LabelAndValue from "../shared/LabelAndValue";
import { styled } from "@mui/material/styles";

const FlexDiv = styled("div")(() => ({
  pointerEvents: "none",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
}));

const Flex1 = styled("div")(() => ({
  flex: 1,
}));

const Forecast = () => {
  const searchValue = useSelector(selectSearchValue);
  const { forecastday } = useSelector(selectWeatherForecastData) || [];

  console.log(forecastday);
  const dispatch = useDispatch();

  useEffect(() => {
    if (searchValue?.name) {
      dispatch(getWeatherData(searchValue));
    }
  }, [searchValue, dispatch]);

  return (
    <WeatherCard maxWidth={920}>
      <CardContent>
        <Grid container spacing={{ xs: 1 }} columns={{ xs: 12 }}>
          {forecastday.map((d) => {
            const {
              day: {
                condition: { text, icon } = {},
                maxtemp_c,
                mintemp_c,
              } = {},
              day,
              date,
            } = d;
            return (
              <Grid item xs={4} key={date}>
                <Paper sx={{ pl: 1, pt: 1, height: "calc(100% - 8px)" }}>
                  <Typography variant="body1" align="center">
                    {m(date).format("dddd")}
                  </Typography>
                  <FlexDiv>
                    <Flex1>
                      <Typography variant="body1">{text}</Typography>
                      <Avatar
                        sx={{ width: 32, height: 32 }}
                        variant="square"
                        src={`https:${icon}`}
                      />
                    </Flex1>
                    <Flex1>
                      <LabelAndValue
                        label="Temperature"
                        value={`${maxtemp_c}-${mintemp_c}`}
                      />
                    </Flex1>
                  </FlexDiv>
                </Paper>
              </Grid>
            );
          })}
        </Grid>
      </CardContent>
    </WeatherCard>
  );
};

export default Forecast;
