import React, { useCallback, useEffect, useState } from "react";
import { styled, alpha } from "@mui/material/styles";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import CircularProgress from "@mui/material/CircularProgress";
import SearchIcon from "@mui/icons-material/Search";
import InputBase from "@mui/material/InputBase";
import { useDispatch } from "react-redux";
import {
  searchLocation,
  selectIsLoading,
  selectOptions,
} from "../../redux/weather/weatherSlice";
import { useSelector } from "react-redux";
import debounce from "lodash/debounce";

function sleep(delay = 0) {
  return new Promise((resolve) => {
    setTimeout(resolve, delay);
  });
}

const SearchWrapper = styled("div")(({ theme }) => ({
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  "&:hover": {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginLeft: 0,
  width: "100%",
}));

const SearchIconWrapper = styled("div")(({ theme }) => ({
  padding: theme.spacing(1, 1),
  position: "absolute",
  pointerEvents: "none",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: "inherit",
  "& .MuiInputBase-input": {
    padding: theme.spacing(1, 1, 1, 0),
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    paddingRight: 30,
  },
}));

const StyledCircularProgress = styled(CircularProgress)(() => ({
  position: "absolute",
  right: 30,
  top: 20,
}));

export default function Seacrh() {
  const dispatch = useDispatch();
  const [open, setOpen] = React.useState(false);
  // const [options, setOptions] = React.useState([]);
  const [searchValue, setSearchValue] = useState("");
  const [debouncedSearchValue, setdebouncedSearchValue] = useState("");
  const [value, setValue] = useState(null);
  const loading = useSelector(selectIsLoading);
  const options = useSelector(selectOptions);
  // const loading = open && options.length === 0;

  const updateDebouncedSearch = useCallback(
    debounce(setdebouncedSearchValue, 300),
    [setdebouncedSearchValue]
  );

  useEffect(() => {
    updateDebouncedSearch(searchValue);
  }, [searchValue, updateDebouncedSearch]);

  useEffect(() => {
    if (debouncedSearchValue) {
      dispatch(searchLocation(debouncedSearchValue));
    }
  }, [debouncedSearchValue, dispatch]);

  const onInputChange = (e) => {
    setSearchValue(e.target.value);
  };

  return (
    <Autocomplete
      value={value}
      onChange={(a, b) => console.log(a, b)}
      disableClearable
      freeSolo
      sx={{ width: 300 }}
      open={open}
      onOpen={() => {
        setOpen(true);
      }}
      onClose={() => {
        setOpen(false);
      }}
      isOptionEqualToValue={(option, value) => option.title === value.title}
      getOptionLabel={(option) => option.name}
      options={options}
      loading={loading}
      renderInput={(params) => {
        const { InputLabelProps, InputProps, ...rest } = params;
        return (
          <SearchWrapper>
            <SearchIconWrapper>
              <SearchIcon />
            </SearchIconWrapper>
            <StyledInputBase
              {...params.InputProps}
              {...rest}
              placeholder="Search"
              value={searchValue}
              onChange={onInputChange}
            />
            {loading ? (
              <StyledCircularProgress color="inherit" size={20} />
            ) : null}
          </SearchWrapper>
        );
      }}
    />
  );
}

// Top films as rated by IMDb users. http://www.imdb.com/chart/top
const topFilms = [
  { title: "The Shawshank Redemption", year: 1994 },
  { title: "The Godfather", year: 1972 },
