import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { selectSearchValue } from "../../redux/locationSearch/locationSearchSlice";
import {
  getCurrentWeather,
  getWeatherData,
  selectWeatherCurrentData,
  selectWeatherData,
} from "../../redux/weather/weatherSlice";
import Box from "@mui/material/Box";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import WeatherCard from "../shared/WeatherCard";
import Avatar from "@mui/material/Avatar";
import LabelAndValue from "../shared/LabelAndValue";

const Now = () => {
  const searchValue = useSelector(selectSearchValue);
  const dispatch = useDispatch();
  const {
    temp_c,
    condition: { text, icon } = {},
    pressure_mb,
    humidity,
    wind_kph,
    wind_dir,
    cloud,
  } = useSelector(selectWeatherCurrentData) || {};

  useEffect(() => {
    if (searchValue) {
      dispatch(getWeatherData(searchValue));
    }
  }, [searchValue, dispatch]);

  return (
    <WeatherCard>
      <CardContent>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
          {text}
        </Typography>
        <Avatar
          sx={{ width: 64, height: 64 }}
          variant="square"
          src={`https:${icon}`}
        />
        <LabelAndValue label="Temperature" value={`${temp_c} &#x2103;`} />
        <Typography variant="h5" component="div">
          {temp_c} &#x2103;
        </Typography>
        <Typography sx={{ mb: 1.5 }} color="text.secondary">
          {pressure_mb}
        </Typography>
        <Typography variant="body2">{humidity}%</Typography>
      </CardContent>
    </WeatherCard>
  );
};

export default Now;
