import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import weatherApi from "../../api/weather";
import { initialState } from "../initialState";

export const getWeatherData = createAsyncThunk(
  "weather/getWeatherData",
  async (searchValue) => {
    const response = await weatherApi.getWeatherData(searchValue);
    // The value we return becomes the `fulfilled` action payload
    return response.data;
  }
);

export const weatherSlice = createSlice({
  name: "weather",
  initialState: initialState.weather,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(getCurrentWeather.pending, (state) => {
        state.loading = true;
      })
      .addCase(getCurrentWeather.fulfilled, (state, action) => {
        state.loading = false;
        state.data = action.payload;
      });
  },
});

export const { incrementByAmount } = weatherSlice.actions;

export const selectWeatherData = (state) => state.weather.data;
export const selectForecastWeatherData = (state) => state.weather.forecastData;

export default weatherSlice.reducer;
